import React, { useState, useEffect } from "react";
import axios from "axios";
import Table from "components/Table/Table.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Card from "components/Card/Card.jsx";
import CardHeader from "components/Card/CardHeader.jsx";
import CardBody from "components/Card/CardBody.jsx";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from "reactstrap";
import "./users.scss";

function Users() {
  const [data, setData] = useState([]);
  const [addUserFormState, setAddUserFormState] = useState(false);
  useEffect(() => {
    (async () => {
      try {
        const result = await axios("http://localhost:8080/users");
        console.log(result.data);
        const usersData = result.data.map(item => [
          item.first_name,
          item.last_name,
          item.user_name,
          item.tasks,
          item.level
        ]);
        console.log(usersData);
        setData(usersData);
      } catch (e) {
        console.log(e);
      }
    })();
  }, []);

  const addUser = e => {
    e.preventDefault();
    axios
      .post("http://localhost:8080/users", {
        firstName: e.target.firstName.value,
        lastName: e.target.lastName.value,
        userName: e.target.userName.value,
        tasks: e.target.tasks.value,
        level: e.target.level.value
      })
      .then(response => {
        if (response.status === 200) {
          axios.get("http://localhost:8080/users").then(result => {
            console.log(result.data);
            const usersData = result.data.map(item => [
              item.first_name,
              item.last_name,
              item.user_name,
              item.tasks,
              item.level
            ]);
            console.log(usersData);
            setData(usersData);
          });
        }
      });
    setAddUserFormState(!addUserFormState);
  };

  const addUserFrame = () => {
    return (
      <div className="add-user">
        <Form className="add-user-form" onSubmit={addUser}>
          <div className="add-user-form-center">
            <FormGroup>
              <Label className="add-user-lable" for="exampleEmail">
                First Name
              </Label>
              <Input
                required
                className="add-user-input"
                type="text"
                placeholder="First Name"
                name="firstName"
              />
            </FormGroup>
            <FormGroup>
              <Label className="add-user-lable" for="exampleEmail">
                Last Name
              </Label>
              <Input
                required
                className="add-user-input"
                type="text"
                name="lastName"
                placeholder="Last Name"
              />
            </FormGroup>
            <FormGroup>
              <Label className="add-user-lable" for="exampleEmail">
                User Name
              </Label>
              <Input
                required
                className="add-user-input"
                type="text"
                name="userName"
                placeholder="User Name"
              />
            </FormGroup>
            <FormGroup>
              <Label className="add-user-lable" for="exampleEmail">
                Tasks
              </Label>
              <Input
                required
                className="add-user-input"
                type="text"
                name="tasks"
                placeholder="Tasks"
              />
            </FormGroup>
            <FormGroup>
              <Label className="add-user-lable" for="exampleEmail">
                Your level
              </Label>
              <Input
                required
                className="add-user-input"
                type="text"
                name="level"
                placeholder="Level"
              />
            </FormGroup>
            <Button color="info" className="add-user-button">
              Submit
            </Button>
          </div>
        </Form>
      </div>
    );
  };

  return (
    <GridContainer>
      {addUserFormState && addUserFrame()}
      {addUserFormState && (
        <Modal
          isOpen={addUserFormState}
          toggle={() => setAddUserFormState(!addUserFormState)}
          className={this.props.className}
        >
          <ModalHeader toggle={() => setAddUserFormState(!addUserFormState)}>
            Modal title
          </ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button
              color="primary"
              onClick={() => setAddUserFormState(!addUserFormState)}
            >
              Do Something
            </Button>{" "}
            <Button
              color="secondary"
              onClick={() => setAddUserFormState(!addUserFormState)}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      )}
      <GridItem xs={12} sm={12} md={12}>
        <Card>
          <CardHeader color="primary">
            <h4>Users list</h4>
            <p>Information about all users</p>
          </CardHeader>
          <CardBody>
            <Table
              tableHeaderColor="primary"
              tableHead={[
                "First Name",
                "Last Name",
                "User name",
                "Tasks",
                "Level"
              ]}
              tableData={data}
            />
          </CardBody>
        </Card>
        <Button
          style={{ display: "block", margin: "auto" }}
          color="info"
          onClick={() => setAddUserFormState(!addUserFormState)}
        >
          Add user
        </Button>
      </GridItem>
    </GridContainer>
  );
}

export default Users;
